import React, { useEffect, useState } from "react";

export default function Motoboys() {
  const [motoboys, setMotoboys] = useState([]);
  const [nome, setNome] = useState("");
  const [telefone, setTelefone] = useState("");

  // Buscar motoboys do backend
  useEffect(() => {
    fetch("http://localhost:5000/api/motoboys")
      .then(res => res.json())
      .then(data => setMotoboys(data))
      .catch(err => console.error("Erro ao carregar motoboys:", err));
  }, []);

  const adicionarMotoboy = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/motoboys", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ nome, telefone }),
      });
      const novo = await res.json();
      setMotoboys([...motoboys, novo]);
      setNome("");
      setTelefone("");
    } catch (err) {
      console.error("Erro ao adicionar motoboy:", err);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Gerenciar Motoboys</h2>

      <form onSubmit={adicionarMotoboy}>
        <input
          type="text"
          placeholder="Nome"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
        />
        <input
          type="text"
          placeholder="Telefone"
          value={telefone}
          onChange={(e) => setTelefone(e.target.value)}
        />
        <button type="submit">Adicionar</button>
      </form>

      <ul>
        {motoboys.map((m) => (
          <li key={m._id}>
            {m.nome} â€” {m.telefone}
          </li>
        ))}
      </ul>
    </div>
  );
}

// Modelo de Pedido
const pedidoSchema = new mongoose.Schema({
  cliente: String,
  endereco: String,
  item: String,
  status: { type: String, default: "pendente" },
  motoboy: { type: mongoose.Schema.Types.ObjectId, ref: "Motoboy", default: null },
  criadoEm: { type: Date, default: Date.now }
});
const Pedido = mongoose.model("Pedido", pedidoSchema);

// Rotas de Pedidos
app.get("/api/pedidos", async (req, res) => {
  const pedidos = await Pedido.find().populate("motoboy");
  res.json(pedidos);
});

app.post("/api/pedidos", async (req, res) => {
  const novoPedido = new Pedido(req.body);
  await novoPedido.save();
  res.json(novoPedido);
});

app.put("/api/pedidos/:id", async (req, res) => {
  const atualizado = await Pedido.findByIdAndUpdate(req.params.id, req.body, { new: true });
  res.json(atualizado);
});

app.delete("/api/pedidos/:id", async (req, res) => {
  await Pedido.findByIdAndDelete(req.params.id);
  res.json({ message: "Pedido removido" });
});
