import React, { useEffect, useState } from "react";

const API_URL = "/api";

function Pedidos() {
  const [pedidos, setPedidos] = useState([]);
  const [novoPedido, setNovoPedido] = useState({ cliente: "", endereco: "", item: "" });

  const carregarPedidos = async () => {
    try {
      const res = await fetch(`${API_URL}/pedidos`);
      const data = await res.json();
      setPedidos(data);
    } catch (err) {
      console.error("âŒ Erro ao carregar pedidos:", err);
    }
  };

  const adicionarPedido = async () => {
    if (!novoPedido.cliente || !novoPedido.item) return;
    try {
      await fetch(`${API_URL}/pedidos`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(novoPedido),
      });
      setNovoPedido({ cliente: "", endereco: "", item: "" });
      carregarPedidos();
    } catch (err) {
      console.error("âŒ Erro ao adicionar pedido:", err);
    }
  };

  useEffect(() => {
    carregarPedidos();
  }, []);

  return (
    <div>
      <h2>ðŸ“¦ Pedidos</h2>
      <input
        placeholder="Cliente"
        value={novoPedido.cliente}
        onChange={(e) => setNovoPedido({ ...novoPedido, cliente: e.target.value })}
      />
      <input
        placeholder="EndereÃ§o"
        value={novoPedido.endereco}
        onChange={(e) => setNovoPedido({ ...novoPedido, endereco: e.target.value })}
      />
      <input
        placeholder="Item"
        value={novoPedido.item}
        onChange={(e) => setNovoPedido({ ...novoPedido, item: e.target.value })}
      />
      <button onClick={adicionarPedido}>Adicionar Pedido</button>

      <ul>
        {pedidos.map((p) => (
          <li key={p._id}>
            <strong>{p.cliente}</strong> â€” {p.item} ({p.status})  
            <br />
            <small>{p.endereco}</small>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Pedidos;
